// client/src/lib/api.js
import axios from "axios";

const BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

export const http = axios.create({
  baseURL: BASE_URL,
  withCredentials: false,
});

// Set/clear the Authorization header globally on our axios instance
export function setAuthToken(token) {
  if (token) {
    http.defaults.headers.common.Authorization = `Bearer ${token}`;
  } else {
    delete http.defaults.headers.common.Authorization;
  }
}

// --- Auth API calls (named exports) ---
export async function registerUser(payload) {
  // expects payload: { email, username, firstName, lastName, password, referralCode? }
  const { data } = await http.post("/auth/signup", payload);
  return data; // { message, token, user }
}

export async function loginUser(payload) {
  // expects payload: { email, password }
  const { data } = await http.post("/auth/login", payload);
  return data; // { message, token, user }
}
