// src/lib/useAppConfig.js
import { useEffect, useState } from 'react';

export function useAppConfig() {
  const [cfg, setCfg] = useState(window.__APP_CONFIG__ || null);

  useEffect(() => {
    if (cfg) return; // already injected
    fetch('/api/config/public')
      .then(r => r.json())
      .then(setCfg)
      .catch(() => setCfg(null));
  }, [cfg]);

  return cfg;
}
