// src/pages/PointsPage.js
import React, { useState, useEffect } from 'react';
import { useAppConfig } from '../lib/useAppConfig';

export default function PointsPage() {
  const cfg = useAppConfig();

  const [balance, setBalance] = useState(0);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Example: fetch points
    fetch('/api/points/me')
      .then(r => r.json())
      .then(data => {
        setBalance(data.pointsBalance);
        setHistory(data.recent);
      });
  }, []);

  if (!cfg) return <div>Loading configâ€¦</div>;

  return (
    <div>
      <h1>Points Page</h1>
      <p>Balance: {balance}</p>
      <p>Trial Length: {cfg.trialDays} days</p>
      <p>Trial Bonus: +{cfg.trialBonusMultiplier * 100}%</p>

      <h3>Recent History</h3>
      <ul>
        {history.map(item => (
          <li key={item._id}>{item.type}: {item.amount}</li>
        ))}
      </ul>
    </div>
  );
}
