import React from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "react-router-dom";
import { api } from "../lib/api";

export default function Trending() {
  const { data, status, error } = useQuery({
    queryKey: ["trending"],
    queryFn: async () => {
      const { data } = await api.get("/feed/trending");
      // expected shape: { categories: [{name,count}], items: [...] }
      return data;
    },
  });

  if (status === "loading") return <div className="feed-status">Loading trending auctions…</div>;
  if (status === "error") return <div className="feed-status">Error: {String(error)}</div>;
  if (!data) return <div className="feed-status">No trending data available</div>;

  return (
    <div className="feed-wrap">
      <header className="feed-header">
        <h1>Trending</h1>
        <p className="muted">Hot categories and auctions right now.</p>
      </header>

      <div className="chip-row">
        {(data?.trendingCategories || []).map((c) => (
          <span className="chip chip-outline" key={c._id}>
            {c._id} • {c.count}
          </span>
        ))}
      </div>

      <div className="feed-grid">
        {(data?.trendingAuctions || []).map((auction) => (
          <article key={auction._id} className="card">
            <div className="thumb">
              <img src={auction.images?.[0] || "/placeholder.png"} alt={auction.title} loading="lazy" />
              <span className="chip">{auction.source?.platform || 'final10'}</span>
            </div>
            <div className="meta">
              <h3 className="title">{auction.title}</h3>
              <div className="row">
                <span className="price">${auction.currentBid || auction.startingPrice}</span>
                <span className="end">Ends {auction.timeRemaining || 'Soon'}</span>
              </div>
              {auction.aiScore && (
                <div className="ai-scores">
                  <span className="score">🔥 {auction.aiScore.trendingScore || 0}</span>
                  <span className="score">💎 {auction.aiScore.dealPotential || 0}</span>
                </div>
              )}
            </div>
            <div className="actions">
              <Link className="btn btn-primary" to={`/auction/${auction._id}`}>
                View Auction
              </Link>
            </div>
          </article>
        ))}
      </div>
    </div>
  );
}
