import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

export default function Register() {
  const { register } = useAuth();
  const nav = useNavigate();
  const [form, setForm] = useState({
    firstName: '', lastName: '', username: '', email: '', password: ''
  });
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      setSubmitting(true);
      await register(form);
      nav('/points'); // go see Savvy Points after signup
    } catch (err) {
      setError(err?.response?.data?.message || err.message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6">
      <h1 className="text-3xl font-bold mb-4">Create your account</h1>
      {error && <div className="mb-3 text-red-400">{error}</div>}
      <form onSubmit={onSubmit} className="space-y-3">
        <input className="input-primary" name="username" placeholder="Username" onChange={onChange} />
        <div className="grid grid-cols-2 gap-3">
          <input className="input-primary" name="firstName" placeholder="First name" onChange={onChange} />
          <input className="input-primary" name="lastName"  placeholder="Last name"  onChange={onChange} />
        </div>
        <input className="input-primary" name="email" type="email" placeholder="Email" onChange={onChange} />
        <input className="input-primary" name="password" type="password" placeholder="Password" onChange={onChange} />
        <button disabled={submitting} className="btn-primary w-full">
          {submitting ? 'Signing up…' : 'Sign up'}
        </button>
      </form>
      <p className="mt-4 text-sm">
        Already have an account? <Link to="/login" className="underline">Sign in</Link>
      </p>
    </div>
  );
}
