import axios from "axios";

// use whatever you already have for API_BASE
export const api = axios.create({
  baseURL: API_BASE,
});

// Use ONE consistent key everywhere
export const STORAGE_KEY = "f10_token";

/** Attach/remove token on axios + persist in localStorage */
export function setAuthToken(token) {
  if (token) {
    localStorage.setItem(STORAGE_KEY, token);
    api.defaults.headers.common.Authorization = `Bearer ${token}`;
  } else {
    localStorage.removeItem(STORAGE_KEY);
    delete api.defaults.headers.common.Authorization;
  }
}

// hydrate on load
const saved = localStorage.getItem(STORAGE_KEY);
if (saved) setAuthToken(saved);

/** ---- Auth ---- **/
export async function loginUser({ email, password }) {
  const { data } = await api.post("/auth/login", { email, password });
  setAuthToken(data.token);
  return data.user;
}

export async function registerUser(payload) {
  const { data } = await api.post("/auth/signup", payload);
  setAuthToken(data.token);
  return data.user;
}

/** NEW: get current user from token */
export async function getMe() {
  const { data } = await api.get("/auth/me");
  return data.user; // { id, email, username, ... }
}

/** ---- Points ---- **/
export async function getMyPoints() {
  const { data } = await api.get("/points/me");
  return data; // { balance, history }
}

export async function claimDailyLogin() {
  const { data } = await api.post("/points/daily");
  return data; // { newBalance, added } or { message }
}
