import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext"; // if you have it

export default function Register() {
  const navigate = useNavigate();
  const { signup, loading } = useAuth?.() || {};
  const [error, setError] = useState("");
  const [form, setForm] = useState({
    username: "",
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    referralCode: "",
  });

  const onChange = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const onSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      if (signup) {
        // AuthContext path
        await signup(form);
      } else {
        // Fallback direct API call if you don't use AuthContext yet
        const res = await fetch("http://localhost:5000/api/auth/signup", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || "Signup failed");
        localStorage.setItem("token", data.token);
      }
      navigate("/auctions");
    } catch (err) {
      setError(err.message || "Signup failed");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0b0b0b] to-[#121212] text-white flex items-center justify-center px-4">
      <div className="w-full max-w-md">
        <div className="mb-6 text-center">
          <div className="mx-auto h-12 w-12 rounded-full bg-gradient-to-br from-yellow-400 to-purple-600 flex items-center justify-center shadow-lg shadow-yellow-500/20">
            <span className="font-bold">⚡</span>
          </div>
          <h1 className="mt-4 text-3xl font-extrabold tracking-tight">
            Create your account
          </h1>
          <p className="mt-2 text-gray-400">Join thousands of smart deal hunters</p>
        </div>

        <form onSubmit={onSubmit} className="card bg-gray-900/60 border border-yellow-500/20 rounded-xl p-6 space-y-4">
          {error ? <p className="text-red-400 text-sm">{error}</p> : null}

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
            <div>
              <label className="block text-sm mb-1 text-gray-300">Username</label>
              <input
                className="input-primary w-full"
                name="username"
                value={form.username}
                onChange={onChange}
                placeholder="yourhandle"
                required
              />
            </div>
            <div>
              <label className="block text-sm mb-1 text-gray-300">First Name</label>
              <input
                className="input-primary w-full"
                name="firstName"
                value={form.firstName}
                onChange={onChange}
                placeholder="Jane"
                required
              />
            </div>
            <div>
              <label className="block text-sm mb-1 text-gray-300">Last Name</label>
              <input
                className="input-primary w-full"
                name="lastName"
                value={form.lastName}
                onChange={onChange}
                placeholder="Doe"
                required
              />
            </div>
            <div className="sm:col-span-2">
              <label className="block text-sm mb-1 text-gray-300">Email</label>
              <input
                type="email"
                className="input-primary w-full"
                name="email"
                value={form.email}
                onChange={onChange}
                placeholder="you@example.com"
                required
              />
            </div>
            <div className="sm:col-span-2">
              <label className="block text-sm mb-1 text-gray-300">Password</label>
              <input
                type="password"
                className="input-primary w-full"
                name="password"
                value={form.password}
                onChange={onChange}
                placeholder="••••••••"
                required
              />
            </div>
            <div className="sm:col-span-2">
              <label className="block text-sm mb-1 text-gray-300">Referral Code (optional)</label>
              <input
                className="input-primary w-full"
                name="referralCode"
                value={form.referralCode}
                onChange={onChange}
                placeholder="welcome"
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full btn-ghost bg-gradient-to-r from-purple-600 to-yellow-500 hover:opacity-95 rounded-lg py-3 font-semibold"
          >
            {loading ? "Creating account..." : "Sign up"}
          </button>

          <p className="text-sm text-gray-400 text-center">
            Already have an account?{" "}
            <Link to="/login" className="text-yellow-400 hover:text-yellow-300 underline">
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}
