import axios from 'axios';

export const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  headers: { 'Content-Type': 'application/json' },
});

// set/remove token globally
export function setAuthToken(token) {
  if (token) {
    api.defaults.headers.common.Authorization = `Bearer ${token}`;
    localStorage.setItem('f10_token', token);
  } else {
    delete api.defaults.headers.common.Authorization;
    localStorage.removeItem('f10_token');
  }
}

// --- Auth ---
export async function registerUser(payload) {
  const { data } = await api.post('/auth/signup', payload);
  return data;
}

export async function loginUser({ email, password }) {
  const { data } = await api.post('/auth/login', { email, password });
  return data;
}

// --- Points ---
export async function getMyPoints() {
  const { data } = await api.get('/points/me');
  return data; // { balance, history }
}

export async function getPointsHistory() {
  const { data } = await api.get('/points/history');
  return data; // [ { amount, note, createdAt }, ... ]
}

export async function claimDailyLogin() {
  const { data } = await api.post('/points/daily');
  return data; // { newBalance, added }
}
