import React from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "react-router-dom";
import ebayService from "../services/ebayService";
import { useAuth } from "../context/AuthContext";

export default function Trending() {
  const { user } = useAuth();

  const { data, status, error } = useQuery({
    queryKey: ["trending"],
    queryFn: async () => {
      try {
        const trendingItems = await ebayService.getTrendingItems('all', 20);
        // expected shape: { trendingAuctions: [...], trendingCategories: [...] }
        return {
          trendingAuctions: trendingItems.items || [],
          trendingCategories: trendingItems.categories || []
        };
      } catch (error) {
        console.error('Error fetching trending items:', error);
        // Return empty data on error to prevent crashes
        return {
          trendingAuctions: [],
          trendingCategories: []
        };
      }
    },
    enabled: !!user, // Only run query if user is logged in
    retry: 1, // Only retry once on failure
    refetchOnWindowFocus: false, // Disable refetch on window focus to prevent rate limiting
  });

  if (!user) {
    return (
      <div className="feed-wrap">
        <header className="feed-header">
          <h1>Trending</h1>
          <p className="muted">Hot categories and auctions right now.</p>
        </header>
        <div className="feed-status">
          <p>Please <Link to="/login">login</Link> to view trending auctions</p>
        </div>
      </div>
    );
  }

  if (status === "loading") return <div className="feed-status">Loading trending auctionsâ€¦</div>;
  if (status === "error") return <div className="feed-status">Error: {String(error)}</div>;
  if (!data) return <div className="feed-status">No trending data available</div>;

  return (
    <div className="feed-wrap">
      <header className="feed-header">
        <h1>Trending</h1>
        <p className="muted">Hot categories and auctions right now.</p>
      </header>

      <div className="chip-row">
        {(data?.trendingCategories || []).map((c, index) => (
          <span className="chip chip-outline" key={`category-${c._id}-${index}`}>
            {c._id} â€¢ {c.count}
          </span>
        ))}
      </div>

      <div className="feed-grid">
        {(data?.trendingAuctions || []).map((auction, index) => (
          <article key={`trending-auction-${auction._id || auction.id || index}-${index}`} className="card">
            <div className="thumb">
              <img 
                src={auction.images?.[0]?.url || "https://via.placeholder.com/400x300/1f2937/8b5cf6?text=Product"} 
                alt={auction.images?.[0]?.alt || auction.title} 
                loading="lazy" 
                onError={(e) => {
                  e.target.src = "https://via.placeholder.com/400x300/1f2937/8b5cf6?text=Product";
                }}
              />
              <span className="chip">eBay</span>
            </div>
            <div className="meta">
              <h3 className="title">{auction.title}</h3>
              <div className="row">
                <span className="price">${auction.currentBid || auction.startingPrice}</span>
                <span className="end">Ends {auction.timeRemaining || 'Soon'}</span>
              </div>
              {auction.aiScore && (
                <div className="ai-scores">
                  <span className="score">ðŸ”¥ {auction.aiScore.trendingScore || 0}</span>
                  <span className="score">ðŸ’Ž {auction.aiScore.dealPotential || 0}</span>
                </div>
              )}
            </div>
            <div className="actions">
              <Link className="btn btn-primary" to={`/auction/${auction._id}`}>
                View Auction
              </Link>
            </div>
          </article>
        ))}
      </div>
    </div>
  );
}
