// client/src/pages/Points.js
import React, { useEffect, useState } from 'react';
import {
  getMyPoints,
  getPointsHistory,
  claimDailyLogin,
  redeemPoints,
} from '../lib/api';

export default function Points() {
  const [balance, setBalance] = useState(0);
  const [activity, setActivity] = useState([]);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [claiming, setClaiming] = useState(false);

  useEffect(() => {
    const load = async () => {
      try {
        const points = await getMyPoints();         // fetch current balance
        const history = await getPointsHistory();   // fetch activity history
        setBalance(points.balance || 0);
        setActivity(history || []);
      } catch (e) {
        setError(e.message);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const onClaim = async () => {
    setError('');
    setMessage('');
    setClaiming(true);
    try {
      const res = await claimDailyLogin();
      setBalance(res.newBalance);
      // Prepend new activity to top (to match UI)
      setActivity(prev => [{ type: 'daily_login', amount: res.earned, note: 'Daily login bonus', createdAt: new Date().toISOString() }, ...prev]);
      setMessage(`+${res.earned} points added!`);
    } catch (e) {
      setError(e.message);
    } finally {
      setClaiming(false);
    }
  };

  if (loading) return <div className="text-gray-300 p-6">Loading points…</div>;

  return (
    <div className="max-w-5xl mx-auto p-6 text-white">
      <h1 className="text-4xl font-extrabold mb-6">
        Savvy <span className="text-yellow-400">Points</span>
      </h1>

      {error && (
        <div className="mb-4 rounded border border-red-500 bg-red-900/20 text-red-300 p-3">
          {error}
        </div>
      )}
      {message && (
        <div className="mb-4 rounded border border-green-500 bg-green-900/20 text-green-300 p-3">
          {message}
        </div>
      )}

      <div className="grid md:grid-cols-3 gap-4 mb-8">
        <div className="rounded-xl border border-yellow-500/40 bg-black/20 p-4">
          <div className="text-sm text-gray-400">Current Balance</div>
          <div className="text-5xl font-bold mt-2">{balance}</div>
        </div>

        <div className="rounded-xl border border-green-500/40 bg-black/20 p-4">
          <div className="text-sm text-gray-400 mb-2">Daily Login</div>
          <button
            onClick={onClaim}
            disabled={claiming}
            className="px-4 py-2 rounded bg-green-600 hover:bg-green-500 disabled:opacity-60"
          >
            {claiming ? 'Claiming…' : "Claim Today's Points"}
          </button>
        </div>

        <div className="rounded-xl border border-purple-500/40 bg-black/20 p-4">
          <div className="text-sm text-gray-400 mb-2">Redeem Points</div>
          <div className="text-xs text-gray-400">* Demo action — hook to coupons/credits later</div>
        </div>
      </div>

      <div className="rounded-xl border border-gray-700 bg-black/20">
        <div className="px-4 py-3 border-b border-gray-700 text-gray-300 font-semibold">
          Recent Activity
        </div>
        <div className="divide-y divide-gray-800">
          {activity.length === 0 ? (
            <div className="p-4 text-gray-500">No point activity yet. Claim your daily login to start!</div>
          ) : (
            activity.map((a, i) => (
              <div key={i} className="p-4 grid grid-cols-4 gap-2 text-sm">
                <div className="text-gray-400">{new Date(a.createdAt || Date.now()).toLocaleString()}</div>
                <div className="capitalize">{a.type.replace('_', ' ')}</div>
                <div className="text-gray-400">{a.note || '-'}</div>
                <div className="text-right font-semibold">+{a.amount}</div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
