import React, { useEffect, useState } from "react";
import {
  getMyPoints,
  getPointsHistory,
  claimDailyLogin,
  redeemPoints,
} from "../lib/api";

export default function Points() {
  const [loading, setLoading] = useState(true);
  const [balance, setBalance] = useState(0);
  const [claimedToday, setClaimedToday] = useState(false);
  const [history, setHistory] = useState([]);
  const [error, setError] = useState("");
  const [redeemAmt, setRedeemAmt] = useState("");

  const ensureAuthed = () => {
    const token = localStorage.getItem("token");
    return !!token;
  };

  async function load() {
    try {
      setError("");
      setLoading(true);
      if (!ensureAuthed()) {
        setError("Please login to view your points.");
        setLoading(false);
        return;
      }
      const [me, hist] = await Promise.all([getMyPoints(), getPointsHistory()]);
      setBalance(me.balance || 0);
      setClaimedToday(!!me.claimedToday);
      setHistory(Array.isArray(hist) ? hist : []);
    } catch (e) {
      setError(e.message || "Failed to load points");
    } finally {
      setLoading(false);
    }
  }
  useEffect(() => {
    const fetchPoints = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/points"); // ðŸ‘ˆ backend port
        const data = await res.json();
        setBalance(data.balance);
        setActivity(data.activity);
      } catch (err) {
        console.error("Error fetching points:", err);
      }
    };
    fetchPoints();
  }, []);
  
  useEffect(() => {
    load();
  }, []);

  async function onClaimDaily() {
    try {
      setError("");
      const res = await claimDailyLogin();
      // optimistic refresh
      setBalance((b) => b + (res.added || 0));
      setClaimedToday(true);
    } catch (e) {
      setError(e.message || "Could not claim daily points");
    }
  }

  async function onRedeem(e) {
    e.preventDefault();
    const amt = parseInt(redeemAmt, 10);
    if (!amt || amt <= 0) return setError("Enter a positive amount to redeem.");
    if (amt > balance) return setError("You donâ€™t have enough points.");

    try {
      setError("");
      const res = await redeemPoints(amt);
      setBalance((b) => b - (res.spent || amt));
      setRedeemAmt("");
      // Optionally reload history
      const hist = await getPointsHistory();
      setHistory(Array.isArray(hist) ? hist : []);
    } catch (e) {
      setError(e.message || "Redeem failed");
    }
  }

  const fmt = (iso) =>
    new Date(iso).toLocaleString(undefined, {
      month: "short",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
    });

  return (
    <div className="min-h-screen px-6 py-10 bg-gradient-to-b from-gray-900 to-black text-white">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl md:text-4xl font-extrabold mb-6">
          Savvy <span className="text-yellow-400">Points</span>
        </h1>

        {error && (
          <div className="mb-6 rounded-md border border-red-500/40 bg-red-900/20 px-4 py-3">
            {error}
          </div>
        )}

        {/* Summary cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
          <div className="rounded-xl border border-yellow-500/30 bg-gray-800/40 p-5">
            <div className="text-sm opacity-75 mb-1">Current Balance</div>
            <div className="text-4xl font-bold text-yellow-300">{loading ? "â€¦" : balance}</div>
          </div>

          <div className="rounded-xl border border-green-500/30 bg-gray-800/40 p-5">
            <div className="text-sm opacity-75 mb-2">Daily Login</div>
            <button
              onClick={onClaimDaily}
              disabled={loading || claimedToday}
              className={`px-4 py-2 rounded-lg font-semibold transition ${
                claimedToday
                  ? "bg-gray-700 text-gray-400 cursor-not-allowed"
                  : "bg-green-600 hover:bg-green-500"
              }`}
            >
              {claimedToday ? "Already Claimed" : "Claim Todayâ€™s Points"}
            </button>
          </div>

          <div className="rounded-xl border border-purple-500/30 bg-gray-800/40 p-5">
            <div className="text-sm opacity-75 mb-2">Redeem Points</div>
            <form onSubmit={onRedeem} className="flex gap-2">
              <input
                type="number"
                min="1"
                value={redeemAmt}
                onChange={(e) => setRedeemAmt(e.target.value)}
                placeholder="Amount"
                className="w-32 rounded-md bg-gray-900 border border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-yellow-500"
              />
              <button
                type="submit"
                className="px-4 py-2 rounded-lg font-semibold bg-purple-600 hover:bg-purple-500 transition"
              >
                Redeem
              </button>
            </form>
            <p className="mt-2 text-xs opacity-70">
              * Demo action â€” hook this to coupons, boosts, or credits.
            </p>
          </div>
        </div>

        {/* History */}
        <div className="rounded-xl border border-gray-700 bg-gray-800/40">
          <div className="px-5 py-4 border-b border-gray-700">
            <h2 className="text-lg font-semibold">Recent Activity</h2>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead>
                <tr className="text-left bg-gray-900/40">
                  <th className="px-5 py-3">When</th>
                  <th className="px-5 py-3">Type</th>
                  <th className="px-5 py-3">Note</th>
                  <th className="px-5 py-3 text-right">Amount</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td className="px-5 py-4 opacity-70" colSpan={4}>
                      Loadingâ€¦
                    </td>
                  </tr>
                ) : history.length === 0 ? (
                  <tr>
                    <td className="px-5 py-6 opacity-70" colSpan={4}>
                      No point activity yet. Claim your daily login to start!
                    </td>
                  </tr>
                ) : (
                  history.map((h) => (
                    <tr key={h._id} className="border-t border-gray-700/70">
                      <td className="px-5 py-3 whitespace-nowrap">{fmt(h.createdAt)}</td>
                      <td className="px-5 py-3 capitalize">{h.type?.replace("_", " ")}</td>
                      <td className="px-5 py-3">{h.note || "-"}</td>
                      <td
                        className={`px-5 py-3 text-right ${
                          h.amount >= 0 ? "text-green-400" : "text-red-400"
                        }`}
                      >
                        {h.amount >= 0 ? `+${h.amount}` : h.amount}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
