import CopyField from "../components/CopyField";
import { makeReferralLink } from "../lib/referrals";
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  Clock, 
  TrendingUp, 
  Bell, 
  Award, 
  Plus,
  Eye,
  Zap
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import CommunityGoals from '../components/CommunityGoals';

const Dashboard = () => {
  const { user } = useAuth();
  const referralLink = user?._id ? makeReferralLink(user._id) : "";

  const quickActions = [
    {
      title: 'Browse Auctions',
      description: 'Find auctions ending soon',
      icon: <Clock className="w-6 h-6" />,
      href: '/auctions',
      color: 'from-yellow-500 to-yellow-400'
    },
    {
      title: 'Create Alert',
      description: 'Set up deal notifications',
      icon: <Bell className="w-6 h-6" />,
      href: '/alerts',
      color: 'from-yellow-600 to-yellow-500'
    },
    {
      title: 'My Points',
      description: 'Check your Savvy Points',
      icon: <Award className="w-6 h-6" />,
      href: '/points',
      color: 'from-yellow-400 to-yellow-300'
    },
    {
      title: 'Create Auction',
      description: 'List your own auction',
      icon: <Plus className="w-6 h-6" />,
      href: '/create-auction',
      color: 'from-yellow-400 to-yellow-300'
    }
  ];

  const stats = [
    { label: 'Savvy Points', value: user?.savvyPoints || 0, icon: <Award className="w-5 h-5" /> },
    { label: 'Active Alerts', value: '3', icon: <Bell className="w-5 h-5" /> },
    { label: 'Auctions Won', value: '12', icon: <TrendingUp className="w-5 h-5" /> },
    { label: 'Time Saved', value: '24h', icon: <Clock className="w-5 h-5" /> }
  ];

  return (
    <div className="min-h-screen bg-gray-900 pt-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="mb-8"
        >
          <h1 className="text-3xl md:text-4xl font-bold text-white mb-2">
            Welcome back, <span className="text-gradient">{user?.firstName}</span>!
          </h1>
