import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

export default function Login() {
  const { login } = useAuth();
  const nav = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      setSubmitting(true);
      await login(email, password);
      nav('/points');
    } catch (err) {
      setError(err?.response?.data?.message || err.message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6">
      <h1 className="text-3xl font-bold mb-4">Welcome back</h1>
      {error && <div className="mb-3 text-red-400">{error}</div>}
      <form onSubmit={onSubmit} className="space-y-3">
        <input className="input-primary" placeholder="Email" type="email"
               value={email} onChange={(e)=>setEmail(e.target.value)} />
        <input className="input-primary" placeholder="Password" type="password"
               value={password} onChange={(e)=>setPassword(e.target.value)} />
        <button disabled={submitting} className="btn-primary w-full">
          {submitting ? 'Signing in…' : 'Sign in'}
        </button>
      </form>
      <p className="mt-4 text-sm">
        Don’t have an account? <Link to="/register" className="underline">Sign up for free</Link>
      </p>
    </div>
  );
}
