import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Clock, Filter, Search, Zap, TrendingUp, Award } from 'lucide-react';

const Auctions = () => {
  const [filters, setFilters] = useState({
    category: '',
    priceRange: '',
    timeRemaining: '',
    sortBy: 'endTime'
  });

  // Mock data for demonstration
  const auctions = [
    {
      id: 1,
      title: 'iPhone 14 Pro Max 256GB - Space Black',
      currentBid: 850,
      timeRemaining: 300, // 5 minutes
      image: 'https://via.placeholder.com/300x200',
      category: 'electronics',
      dealPotential: 95,
      competitionLevel: 'low',
      trendingScore: 88,
      platform: 'ebay',
      bidCount: 3
    },
    {
      id: 2,
      title: 'Nike Air Jordan 1 Retro High OG',
      currentBid: 120,
      timeRemaining: 180, // 3 minutes
      image: 'https://via.placeholder.com/300x200',
      category: 'fashion',
      dealPotential: 87,
      competitionLevel: 'medium',
      trendingScore: 92,
      platform: 'mercari',
      bidCount: 7
    },
    {
      id: 3,
      title: 'PlayStation 5 Console - Brand New',
      currentBid: 450,
      timeRemaining: 480, // 8 minutes
      image: 'https://via.placeholder.com/300x200',
      category: 'electronics',
      dealPotential: 78,
      competitionLevel: 'high',
      trendingScore: 95,
      platform: 'facebook',
      bidCount: 12
    }
  ];

  const formatTimeRemaining = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}m ${remainingSeconds}s`;
  };

  const getCompetitionColor = (level) => {
    switch (level) {
      case 'low': return 'text-green-400';
      case 'medium': return 'text-yellow-400';
      case 'high': return 'text-red-400';
      default: return 'text-gray-400';
    }
  };

  const getDealPotentialColor = (score) => {
    if (score >= 90) return 'text-green-400';
    if (score >= 70) return 'text-yellow-400';
    return 'text-red-400';
  };

  return (
    <div className="min-h-screen bg-gray-900 pt-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="mb-8"
        >
          <h1 className="text-3xl md:text-4xl font-bold text-white mb-4">
            Live <span className="text-gradient">Auctions</span>
          </h1>
          <p className="text-gray-400 text-lg">
            AI-curated auctions ending in 10 minutes or less
          </p>
        </motion.div>

        {/* Filters */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
          className="card mb-8"
        >
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex items-center gap-2">
              <Filter className="w-5 h-5 text-gray-400" />
              <span className="text-white font-medium">Filters:</span>
            </div>
            
            <select
              value={filters.category}
              onChange={(e) => setFilters({...filters, category: e.target.value})}
              className="input-primary"
            >
              <option value="">All Categories</option>
              <option value="electronics">Electronics</option>
              <option value="fashion">Fashion</option>
              <option value="home">Home</option>
              <option value="sports">Sports</option>
            </select>

            <select
              value={filters.timeRemaining}
              onChange={(e) => setFilters({...filters, timeRemaining: e.target.value})}
              className="input-primary"
            >
              <option value="">Any Time</option>
              <option value="5">5 minutes or less</option>
              <option value="10">10 minutes or less</option>
            </select>

            <select
              value={filters.sortBy}
              onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
              className="input-primary"
            >
              <option value="endTime">Ending Soon</option>
              <option value="dealPotential">Best Deals</option>
              <option value="trending">Most Trending</option>
              <option value="price">Price</option>
            </select>
          </div>
        </motion.div>

        {/* Auctions Grid */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-6"
        >
          {auctions.map((auction, index) => (
            <motion.div
              key={auction.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              className="card-hover group cursor-pointer"
            >
              {/* Image */}
              <div className="relative mb-4">
                <img
                  src={auction.image}
                  alt={auction.title}
                  className="w-full h-48 object-cover rounded-lg"
                />
                <div className="absolute top-3 left-3 bg-black/70 text-white px-2 py-1 rounded text-sm font-medium">
                  {auction.platform}
                </div>
                <div className="absolute top-3 right-3 bg-red-500 text-white px-2 py-1 rounded text-sm font-medium flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  {formatTimeRemaining(auction.timeRemaining)}
                </div>
              </div>

              {/* Content */}
              <div className="space-y-3">
                <h3 className="text-lg font-semibold text-white group-hover:text-purple-400 transition-colors line-clamp-2">
                  {auction.title}
                </h3>

                {/* AI Scores */}
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <div className="flex items-center gap-1">
                      <TrendingUp className="w-4 h-4 text-purple-400" />
                      <span className={`text-sm font-medium ${getDealPotentialColor(auction.dealPotential)}`}>
                        {auction.dealPotential}%
                      </span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Zap className="w-4 h-4 text-yellow-400" />
                      <span className={`text-sm font-medium ${getCompetitionColor(auction.competitionLevel)}`}>
                        {auction.competitionLevel}
                      </span>
                    </div>
                  </div>
                  <div className="flex items-center gap-1">
                    <Award className="w-4 h-4 text-blue-400" />
                    <span className="text-sm font-medium text-blue-400">
                      {auction.trendingScore}%
                    </span>
                  </div>
                </div>

                {/* Price and Bids */}
                <div className="flex items-center justify-between">
                  <div>
                    <div className="text-2xl font-bold text-white">
                      ${auction.currentBid}
                    </div>
                    <div className="text-sm text-gray-400">
                      {auction.bidCount} bids
                    </div>
                  </div>
                  <button className="btn-primary">
                    Place Bid
                  </button>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Load More */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          className="text-center mt-8"
        >
          <button className="btn-secondary">
            Load More Auctions
          </button>
        </motion.div>
      </div>
    </div>
  );
};

export default Auctions;
