// client/src/pages/PointsPage.jsx
import React, { useEffect, useState } from "react";
import { getMyPoints, claimDailyLogin } from "../lib/api";

export default function PointsPage() {
  const [loading, setLoading] = useState(true);
  const [balance, setBalance] = useState(0);
  const [history, setHistory] = useState([]);
  const [msg, setMsg] = useState("");
  const [err, setErr] = useState("");
  const [claiming, setClaiming] = useState(false);

  async function load() {
    setErr(""); setMsg("");
    try {
      const res = await getMyPoints();
      setBalance(res.balance ?? 0);
      setHistory(res.history ?? []);
    } catch (e) {
      setErr(e?.response?.data?.message || e.message);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); }, []);

  async function onClaim() {
    setErr(""); setMsg(""); setClaiming(true);
    try {
      const res = await claimDailyLogin();
      if (res.newBalance !== undefined) {
        setBalance(res.newBalance);
        setMsg(`+${res.added} points added`);
        // you could also prepend a fake history row if your API doesn't yet
      } else if (res.message) {
        setMsg(res.message);
      }
    } catch (e) {
      setErr(e?.response?.data?.message || e.message);
    } finally {
      setClaiming(false);
    }
  }

  if (loading) return <div style={{padding:16}}>Loading points…</div>;

  return (
    <div style={{maxWidth:900, margin:"40px auto", padding:"0 16px"}}>
      <h1>Savvy Points</h1>

      {err && (
        <div style={{background:"#fee", border:"1px solid #f99", padding:12, margin:"12px 0"}}>
          {err}
        </div>
      )}
      {msg && (
        <div style={{background:"#efe", border:"1px solid #9f9", padding:12, margin:"12px 0"}}>
          {msg}
        </div>
      )}

      <div style={{display:"grid", gridTemplateColumns:"1fr 1fr", gap:16, marginTop:12}}>
        <div style={{border:"1px solid #ddd", borderRadius:8, padding:16}}>
          <div style={{opacity:.6, marginBottom:8}}>Current Balance</div>
          <div style={{fontSize:36, fontWeight:800}}>{balance}</div>
        </div>

        <div style={{border:"1px solid #ddd", borderRadius:8, padding:16}}>
          <div style={{opacity:.6, marginBottom:8}}>Daily Login</div>
          <button
            onClick={onClaim}
            disabled={claiming}
            style={{
              padding:"10px 14px",
              borderRadius:8,
              border:"none",
              cursor:"pointer",
            }}
          >
            {claiming ? "Claiming…" : "Claim Today's Points"}
          </button>
        </div>
      </div>

      <div style={{marginTop:24, border:"1px solid #ddd", borderRadius:8}}>
        <div style={{padding:12, borderBottom:"1px solid #eee", fontWeight:700}}>
          Recent Activity
        </div>
        <div style={{padding:12}}>
          {history?.length ? (
            <table width="100%" style={{borderCollapse:"collapse"}}>
              <thead>
                <tr>
                  <th align="left">When</th>
                  <th align="left">Type</th>
                  <th align="left">Note</th>
                  <th align="right">Amount</th>
                </tr>
              </thead>
              <tbody>
                {history.map((h, i) => (
                  <tr key={i} style={{borderTop:"1px solid #eee"}}>
                    <td>{new Date(h.createdAt || h.created || Date.now()).toLocaleString()}</td>
                    <td>{h.type}</td>
                    <td>{h.note}</td>
                    <td align="right">{h.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <div style={{opacity:.7}}>No point activity yet. Claim your daily login to start!</div>
          )}
        </div>
      </div>
    </div>
  );
}
