import React from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "react-router-dom";
import { api } from "../lib/api";

export default function Trending() {
  const { data, status, error } = useQuery({
    queryKey: ["trending"],
    queryFn: async () => {
      const { data } = await api.get("/feed/trending");
      // expected shape: { categories: [{name,count}], items: [...] }
      return data;
    },
  });

  if (status === "loading") return <div className="feed-status">Loading…</div>;
  if (status === "error") return <div className="feed-status">Error: {String(error)}</div>;

  return (
    <div className="feed-wrap">
      <header className="feed-header">
        <h1>Trending</h1>
        <p className="muted">Hot categories and auctions right now.</p>
      </header>

      <div className="chip-row">
        {(data.categories || []).map((c) => (
          <span className="chip chip-outline" key={c.name}>
            {c.name} • {c.count}
          </span>
        ))}
      </div>

      <div className="feed-grid">
        {(data.items || []).map((it) => (
          <article key={it.id} className="card">
            <div className="thumb">
              <img src={it.image || "/placeholder.png"} alt={it.title} loading="lazy" />
              {it.platform && <span className="chip">{it.platform}</span>}
            </div>
            <div className="meta">
              <h3 className="title">{it.title}</h3>
              <div className="row">
                <span className="price">${it.currentPrice ?? it.price}</span>
                <span className="end">Ends {it.endsIn || it.endsAtHuman}</span>
              </div>
            </div>
            <div className="actions">
              {it.auctionId ? (
                <Link className="btn btn-primary" to={`/auctions/${it.auctionId}`}>Open Auction</Link>
              ) : (
                <a className="btn btn-primary" href={it.url} target="_blank" rel="noreferrer">Open</a>
              )}
            </div>
          </article>
        ))}
      </div>
    </div>
  );
}
