import axios from 'axios';

const BASE = '/api';

function authHeaders() {
  const t = localStorage.getItem('token');
  return t ? { Authorization: `Bearer ${t}` } : {};
}

// login / signup (already done)
export async function loginUser(email, password) {
  const { data } = await axios.post(`${BASE}/auth/login`, { email, password });
  localStorage.setItem('token', data.token);
  return data.user;
}
export async function registerUser(body) {
  const { data } = await axios.post(`${BASE}/auth/signup`, body);
  localStorage.setItem('token', data.token);
  return data.user;
}

// points
export async function getMyPoints() {
  const { data } = await axios.get(`${BASE}/points/me`, { headers: authHeaders() });
  return data; // { balance, history }
}

export async function claimDailyLogin() {
  const { data } = await axios.post(`${BASE}/points/daily`, {}, { headers: authHeaders() });
  return data; // { newBalance, earned, activity }
}

export async function getPointsHistory() {
  const { data } = await axios.get(`${BASE}/points/history`, { headers: authHeaders() });
  return data; // [{_id, type, amount, note, createdAt}, ...]
}

export async function redeemPoints(amount) {
  const { data } = await axios.post(`${BASE}/points/redeem`, { amount }, { headers: authHeaders() });
  return data;
}

// ---- (Optional) ALERTS – keep these to satisfy existing imports ----
export async function getAlerts() {
  const { data } = await axios.get(`${BASE}/alerts`, { headers: authHeaders() });
  return data;
}
export async function createAlert(payload) {
  const { data } = await axios.post(`${BASE}/alerts`, payload, { headers: authHeaders() });
  return data;
}
export async function deleteAlert(id) {
  const { data } = await axios.delete(`${BASE}/alerts/${id}`, { headers: authHeaders() });
  return data;
}
export async function toggleAlert(id, enabled) {
  const { data } = await axios.patch(`${BASE}/alerts/${id}`, { enabled }, { headers: authHeaders() });
  return data;
}
